import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
    const [serverIp, setServerIp] = useState(null);
    const [domain, setDomain] = useState("");
    const [resolvedIp, setResolvedIp] = useState([]);

    // ✅ Fetch all stored resolved IPs when the app loads
    useEffect(() => {
        const fetchStoredIps = async () => {
            try {
                const response = await axios.get("http://localhost:8000/api/all_resolved");
                const storedData = response.data;
                console.log("response", response.data);
                console.log("Stored IPs:", storedData);
                const formattedData = Object.keys(storedData).map(domain => ({
                    domain,
                    ipv4: storedData[domain][0].length > 0 ? storedData[domain][0].join(", ") : "N/A",
                    ipv6: storedData[domain][1].length > 0 ? storedData[domain][1].join(", ") : "N/A"
                }));

                setResolvedIp(formattedData);
            } catch (error) {
                console.error("Error fetching stored IPs:", error);
            }
        };

        fetchStoredIps();
    }, []);

    // ✅ Resolve domain & store in backend
    const resolveDomain = async () => {
        if (!domain) return;

        // Prevent duplicate domains in frontend (backend already caches them)
        if (resolvedIp.some(entry => entry.domain === domain)) {
            alert(`The domain "${domain}" has already been resolved.`);
            return;
        }

        try {
            const response = await axios.get(`http://localhost:8000/api/resolve/${domain}`);

            const ipv4 = response.data[0].length > 0 ? response.data[0].join(", ") : "N/A";
            const ipv6 = response.data[1].length > 0 ? response.data[1].join(", ") : "N/A";

            // ✅ Update frontend list
            setResolvedIp(prev => [...prev, { domain, ipv4, ipv6 }]);

            console.log("Resolved IP:", { domain, ipv4, ipv6 });
        } catch (error) {
            console.error("Error resolving domain:", error);
        }
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
            <h1>IP Address Finder</h1>

            <h2>Resolve Domain</h2>
            <input
                type="text"
                value={domain}
                onChange={(e) => setDomain(e.target.value)}
                placeholder="Enter domain (e.g., google.com)"
            />
            <button onClick={resolveDomain}>Resolve</button>

            {/* Display Stored Resolved Domains */}
            {resolvedIp.length > 0 && (
                <div>
                    <h3>Resolved Domains (Saved in Backend Memory):</h3>
                    {resolvedIp.map((entry, index) => (
                        <div key={index}>
                            <strong>{entry.domain}</strong>
                            <ul>
                                <li>IPv4: {entry.ipv4}</li>
                                <li>IPv6: {entry.ipv6}</li>
                            </ul>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default App;
