import React, { useState } from "react";
import axios from "axios";

function App() {
    const [serverIp, setServerIp] = useState(null);
    const [domain, setDomain] = useState("");
    const [resolvedIp, setResolvedIp] = useState([]);

    // Fetch server's IPs
    const fetchServerIp = async () => {
        try {
            const response = await axios.get("http://localhost:8000/api/ip");
            console.log("Server IP:", response.data);
            setResolvedIp(prev => [...prev, { domain, ...response.data }]);
        } catch (error) {
            console.error("Error fetching server IP:", error);
        }
    };

    // Resolve domain IPs
    const resolveDomain = async () => {
        if (!domain) return;
        try {
            const response = await axios.get(`http://localhost:8000/api/resolve/${domain}`);
            setResolvedIp(response.data);
            console.log("Resolved IP:", response.data);
        } catch (error) {
            console.error("Error resolving domain:", error);
        }
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
            <h1>IP Address Finder</h1>

            <button onClick={fetchServerIp} style={{ marginBottom: "10px" }}>Get Server IP</button>
            {serverIp && (
                <div>
                    <p><strong>Internal IP:</strong> {serverIp.internal_ip}</p>
                    <p><strong>External IP:</strong> {serverIp.external_ip}</p>
                </div>
            )}

            <h2>Resolve Domain</h2>
            <input
                type="text"
                value={domain}
                onChange={(e) => setDomain(e.target.value)}
                placeholder="Enter domain (e.g., google.com)"
            />
            <button onClick={resolveDomain}>Resolve</button>

            {resolvedIp.ipv4 && resolvedIp.ipv4.length > 0 && (
                <div>
                    <h3>IPv4 Addresses:</h3>
                    <ul>
                        {resolvedIp[0]}
                    </ul>
                </div>
            )}

            {resolvedIp.ipv6 && resolvedIp.ipv6.length > 0 && (
                <div>
                    <h3>IPv6 Addresses:</h3>
                    <ul>
                        {resolvedIp[0]}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default App;
