import React, { useState } from "react";
import axios from "axios";

function App() {
    const [serverIp, setServerIp] = useState(null);
    const [domain, setDomain] = useState("");
    const [resolvedDomains, setResolvedDomains] = useState([]); // Store resolved domains
    const [ipv4Address, setIpv4Address] = useState([]);
    const [ipv6Address, setIpv6Address] = useState([]);

    // Fetch server's IPs
    const fetchServerIp = async () => {
        try {
            const response = await axios.get("http://localhost:8000/api/ip");
            setServerIp(response.data);
        } catch (error) {
            console.error("Error fetching server IP:", error);
        }
    };

    // Resolve domain IPs
    const resolveDomain = async () => {
        if (!domain) return;

        // ✅ Check for duplicate domains
        if (resolvedDomains.includes(domain)) {
            alert(`The domain "${domain}" has already been resolved.`);
            return;
        }

        try {
            const response = await axios.get(`http://localhost:8000/api/resolve/${domain}`);

            // ✅ Ensure response is an array: [ipv4, ipv6]
            const ipv4 = response.data[0] || "N/A";  // Handle empty responses
            const ipv6 = response.data[1] || "N/A";

            // ✅ Update state while preserving previous values
            setResolvedDomains(prev => [...prev, domain]);  
            setIpv4Address(prev => [...prev, { domain, address: ipv4 }]);
            setIpv6Address(prev => [...prev, { domain, address: ipv6 }]);

            console.log("Resolved IP:", response.data);
        } catch (error) {
            console.error("Error resolving domain:", error);
        }
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
            <h1>IP Address Finder</h1>

            <button onClick={fetchServerIp} style={{ marginBottom: "10px" }}>Get Server IP</button>
            {serverIp && (
                <div>
                    <p><strong>Internal IP:</strong> {serverIp.internal_ip}</p>
                    <p><strong>External IP:</strong> {serverIp.external_ip}</p>
                </div>
            )}

            <h2>Resolve Domain</h2>
            <input
                type="text"
                value={domain}
                onChange={(e) => setDomain(e.target.value)}
                placeholder="Enter domain (e.g., google.com)"
            />
            <button onClick={resolveDomain}>Resolve</button>

            {/* Display IPv4 Addresses */}
            {ipv4Address.length > 0 && (
                <div>
                    <h3>Resolved Domains (IPv4 & IPv6):</h3>
                    {ipv4Address.map((entry, index) => (
                        <div key={index}>
                            <strong>{entry.domain}</strong>
                            <ul>
                                <li>IPv4: {entry.address}</li>
                                <li>IPv6: {ipv6Address[index]?.address || "N/A"}</li>
                            </ul>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default App;
